const i = JSON.parse('{"title":"隐私政策","description":"你好啊","frontmatter":{},"headers":[{"level":2,"title":"我们收集您的何种信息？","slug":"whats-message-doeswe-collect","link":"#whats-message-doeswe-collect","children":[]},{"level":2,"title":"我们利用您的个人信息作何用途？","slug":"how-we-use-your-personal-secret","link":"#how-we-use-your-personal-secret","children":[]},{"level":2,"title":"我们用到的第三方SDK？","slug":"whats-sdk-do-we-use","link":"#whats-sdk-do-we-use","children":[]}],"relativePath":"privacy-policy/index.md","filePath":"privacy-policy/index.md"}')
